    private void btnAdicionarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAdicionarActionPerformed
        try {
            Connection con = conexao.abrirConexao();
            profBean pb = new profBean();
            profDAO pd = new profDAO(con);
            
            pb.setCpf(txtCPF.getText());
            pb.setNome(txtNome.getText());
            pb.setIdade(Integer.parseInt(txtIdade.getText()));
            pb.setFuncao(txtFuncao.getText());
            pb.setEmail(txtEmail.getText());
            
            lblResult2.setText(pd.inserir(pb));
            
            conexao.fecharConexao(con);
        } catch (SQLException e){
            JOptionPane.showMessageDialog(null, "Dados incorretos " + e.getMessage());
        } catch (Exception e){
            JOptionPane.showMessageDialog(null, "Erro... " + e.getMessage());
        }
    
    }//GEN-LAST:event_btnAdicionarActionPerformed

    private void btnLimparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLimparActionPerformed
        txtCPF.setText("");
        txtNome.setText("");
        txtIdade.setText("");
        txtFuncao.setText("");
        txtEmail.setText("");
        lblResult2.setText("");
        DefaultTableModel tbm = (DefaultTableModel)tblConsulta.getModel();
        for (int i = tbm.getRowCount()-1; i >= 0; i--){
            tbm.removeRow(i);
        }
    }//GEN-LAST:event_btnLimparActionPerformed

