    private void btnAlterarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAlterarActionPerformed
        try {
            Connection con = conexao.abrirConexao();
            profBean pb = new profBean();
            profDAO pd = new profDAO(con);

            pb.setCpf(txtCPF.getText());
            pb.setNome(txtNome.getText());
            pb.setIdade(Integer.parseInt(txtIdade.getText()));
            pb.setFuncao(txtFuncao.getText());
            pb.setEmail(txtEmail.getText());
            
            lblResult2.setText(pd.alterar(pb));
            
            conexao.fecharConexao(con);
        } catch (SQLException e){
            JOptionPane.showMessageDialog(null, "Dados incorretos" + e.getMessage());
        } catch (Exception e){
            JOptionPane.showMessageDialog(null, "Erro... " + e.getMessage());
        }
    }//GEN-LAST:event_btnAlterarActionPerformed

    private void btnExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExcluirActionPerformed
        try {
            Connection con = conexao.abrirConexao();
            profBean pb = new profBean();
            profDAO pd = new profDAO(con);
            pb.setCpf(txtCPF.getText());
            Object[] opcoes = { "Sim", "Não"};
            int i = JOptionPane.showOptionDialog(null, "Desela excluir esse professor ?" +
                    "" + txtCPF.getText() + " ? ", "Exclusão",
                    JOptionPane.YES_NO_OPTION,
                    JOptionPane.QUESTION_MESSAGE, null,
                    opcoes, opcoes[0]);
            if (i == JOptionPane.YES_OPTION){
                lblResult2.setText(pd.excluir(pb));
            }
            conexao.fecharConexao(con);
        } catch (SQLException e){
            JOptionPane.showMessageDialog(null, "Erro..."+e.getMessage());
        }
    }//GEN-LAST:event_btnExcluirActionPerformed

    private void btnTodosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTodosActionPerformed
        try {
            Connection con = conexao.abrirConexao();
            profDAO pd = new profDAO(con);
            List<profBean> listaProf = new ArrayList<profBean>();
            listaProf = pd.ListarTodos();
            DefaultTableModel tbm = (DefaultTableModel) tblConsulta.getModel();
            for (int i = tbm.getRowCount() - 1; i >= 0; i--) {
                tbm.removeRow(i);
            }
            int i = 0;
            for (profBean pb : listaProf) {
                tbm.addRow(new String[1]);
                tblConsulta.setValueAt(pb.getCpf(), i, 0);
                tblConsulta.setValueAt(pb.getNome(), i, 1);
                tblConsulta.setValueAt(pb.getIdade(), i, 2);
                tblConsulta.setValueAt(pb.getFuncao(), i, 3);
                tblConsulta.setValueAt(pb.getEmail(), i, 4);
                i++;
            }
            conexao.fecharConexao(con);
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Erro..." + e.getMessage());
        }
    }//GEN-LAST:event_btnTodosActionPerformed

